import React from "react";

const Card = ({ backgroundImage, profileImage, title, priceRange }) => {
  return (
    <div className="w-[280px] sm:w-[300px] xl:w-[304px] h-[350px] sm:h-[370px] xl:h-[377px] flex flex-col gap-[20px] xl:gap-[24px] bg-cover bg-center">
      <div 
        className="relative p-3 xl:p-4 w-[280px] sm:w-[300px] xl:w-[304px] h-[270px] sm:h-[290px] xl:h-[296px] flex justify-center rounded-[8px] xl:rounded-[0px]" 
        style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        <img
          src={profileImage}
          alt={`${title} representative`}
          className="absolute bottom-3 xl:bottom-4 left-3 xl:left-4 rounded-full w-24 h-24 sm:w-28 sm:h-28 xl:w-32 xl:h-32 object-cover border-2 border-white shadow-lg"
        />
      </div>
      <div className="w-[272px] sm:w-[292px] xl:w-[296px] min-h-[50px] xl:h-[57px] px-2 xl:px-0">
        <h3 className="text-[18px] xl:text-[20px] leading-[24px] xl:leading-[30px] font-semibold text-center xl:text-left mb-1">
          {title}
        </h3>
        <p className="text-[#666666] text-[16px] xl:text-[18px] leading-[22px] xl:leading-[27px] font-normal text-center xl:text-left">
          {priceRange}
        </p>
      </div>
    </div>
  );
};

export default Card;
