import useButtonAction from "../../hooks/useButtonAction";

function Component(){
    const { handleStartHiring } = useButtonAction();

    return(
        <div className="w-full max-w-[1440px] xl:w-[1440px] mx-auto min-h-[300px] xl:h-[479px] py-[56px] xl:py-[112px] px-4 sm:px-8 xl:px-[64px] text-[#444444] bg-cover bg-center" style={{ backgroundImage: `url('/images/29.png')` }} >
            <div className="w-full max-w-[768px] xl:w-[768px] min-h-[200px] xl:h-[255px] flex flex-col gap-[16px] xl:gap-[24px] mx-auto">
                <div className="w-full xl:w-[768px] min-h-[120px] xl:h-[167px] flex flex-col gap-[16px] xl:gap-[24px]">
                    <h2 className="font-semibold text-[28px] sm:text-[36px] xl:text-[48px] leading-[1.2] xl:leading-[57.5px] text-center font-[sans-serif] text-[#000000] px-4 xl:px-0">Ready to explore hiring remote LatAm talent?</h2>
                    <p className="font-normal text-[16px] xl:text-[18px] leading-[1.5] xl:leading-[27px] text-center font-[sans-serif] text-[#000000] max-w-[600px] xl:max-w-none mx-auto px-4 xl:px-0">Find out if Latin Remote Workers is a good fit for you in just 30 minutes.</p>
                </div>
                <div className="w-full flex justify-center pt-[16px]">
                    <button 
                        className="rounded-[12px] py-[12px] px-[24px] text-[13px] xl:text-[15px] font-normal text-center font-[sans-serif] bg-[#000000] text-white transition-all hover:bg-[#2a2a2a] hover:scale-105"
                        onClick={handleStartHiring}
                    >
                        Start hiring
                    </button>
                </div>
            </div>
        </div>
    );
}
export default Component;
