import React from 'react';
import OptimizedImage from '../OptimizedImage';
import './slide.css'; // Import CSS specific to Slide component

function Slide({ title, description, image, additionalImage, additionalDescription , style }) {
  return (
    <div className="slide transition-all duration-300 ease-in-out p-4 sm:p-6 lg:p-8 bg-white rounded-[16px] shadow-sm border border-gray-100 mx-4" style={style}>
      {/* Company Logo */}
      <div className="flex justify-center mb-4 lg:mb-6">
        <OptimizedImage 
          className='max-w-[120px] sm:max-w-[140px] lg:max-w-[158px] h-auto object-contain' 
          src={image} 
          alt={`${description} company logo`} 
          width={158} 
          height={56}
        />
      </div>

      {/* Testimonial Text */}
      <blockquote className='text-center mb-6 lg:mb-8'>
        <p className='text-[16px] sm:text-[18px] lg:text-[20px] xl:text-[24px] font-medium font-[sans-serif] leading-[1.4] text-[#333333] italic'>
          {title}
        </p>
      </blockquote>

      {/* Author Information */}
      <div className='flex flex-col items-center gap-3 lg:gap-4'>
        {additionalImage && (
          <OptimizedImage 
            src={additionalImage} 
            alt={`${description} profile`} 
            width={56}
            height={56}
            className="w-12 h-12 lg:w-14 lg:h-14 rounded-full object-cover border-2 border-gray-200"
          />
        )}
        <div className="text-center">
          <p className='font-[sans-serif] font-bold text-[16px] lg:text-[18px] text-[#000000] mb-1'>
            {description}
          </p>
          <p className='text-[#666666] font-[sans-serif] text-[14px] lg:text-[16px]'>
            {additionalDescription}
          </p>
        </div>
      </div>
    </div>
  );
}

export default Slide;
