import React, { useState } from 'react'
import CompanyLogo from '../icons/company-logo'
import useButtonAction from '../../hooks/useButtonAction'
import './index.css'

const Nav = () => {
  const { handleStartHiring, handleNavigation } = useButtonAction();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <div className='gradient-border w-full max-w-[1440px] mx-auto'>
      <div className='flex items-center justify-between px-4 sm:px-8 lg:px-[64px] h-[72px]'>
        {/* Logo */}
        <div className="flex-shrink-0">
          <CompanyLogo />
        </div>

        {/* Desktop Navigation */}
        <div className='hidden lg:flex items-center gap-[32px]'>
          <span 
            className='cursor-pointer underline-custom text-sm xl:text-base'
            onClick={() => handleNavigation('how-it-works')}
          > 
            How it works
          </span>
          <span 
            className='cursor-pointer underline-custom text-sm xl:text-base'
            onClick={() => handleNavigation('pricing')}
          >
            Pricing
          </span>
          <span 
            className='cursor-pointer underline-custom text-sm xl:text-base'
            onClick={() => handleNavigation('roles')}
          >
            Roles
          </span>
          <span 
            className="cursor-pointer underline-custom text-sm xl:text-base"
            onClick={() => handleNavigation('our-promise')}
          >
            Our promise
          </span>
          <button 
            className='text-white px-[20px] py-[8px] rounded-[10px] bg-[#231F20] transition-all hover:bg-[#3a3a3a] hover:scale-105 text-sm xl:text-base'
            onClick={handleStartHiring}
          >
            Start Hiring
          </button>
        </div>

        {/* Mobile Menu Button */}
        <button
          className='lg:hidden flex flex-col justify-center items-center w-8 h-8'
          onClick={toggleMobileMenu}
          aria-label="Toggle mobile menu"
        >
          <div className={`w-6 h-0.5 bg-[#231F20] transition-all duration-300 ${isMobileMenuOpen ? 'rotate-45 translate-y-1.5' : ''}`}></div>
          <div className={`w-6 h-0.5 bg-[#231F20] transition-all duration-300 mt-1 ${isMobileMenuOpen ? 'opacity-0' : ''}`}></div>
          <div className={`w-6 h-0.5 bg-[#231F20] transition-all duration-300 mt-1 ${isMobileMenuOpen ? '-rotate-45 -translate-y-1.5' : ''}`}></div>
        </button>
      </div>

      {/* Mobile Menu */}
      <div className={`lg:hidden transition-all duration-300 ease-in-out ${isMobileMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'} overflow-hidden bg-white border-t border-gray-200`}>
        <div className='flex flex-col py-4 px-4 sm:px-8 gap-4'>
          <span 
            className='cursor-pointer underline-custom text-base py-2 border-b border-gray-100'
            onClick={() => {
              handleNavigation('how-it-works');
              setIsMobileMenuOpen(false);
            }}
          > 
            How it works
          </span>
          <span 
            className='cursor-pointer underline-custom text-base py-2 border-b border-gray-100'
            onClick={() => {
              handleNavigation('pricing');
              setIsMobileMenuOpen(false);
            }}
          >
            Pricing
          </span>
          <span 
            className='cursor-pointer underline-custom text-base py-2 border-b border-gray-100'
            onClick={() => {
              handleNavigation('roles');
              setIsMobileMenuOpen(false);
            }}
          >
            Roles
          </span>
          <span 
            className="cursor-pointer underline-custom text-base py-2 border-b border-gray-100"
            onClick={() => {
              handleNavigation('our-promise');
              setIsMobileMenuOpen(false);
            }}
          >
            Our promise
          </span>
          <button 
            className='text-white px-[20px] py-[12px] rounded-[10px] bg-[#231F20] transition-all hover:bg-[#3a3a3a] hover:scale-105 text-base mt-2'
            onClick={() => {
              handleStartHiring();
              setIsMobileMenuOpen(false);
            }}
          >
            Start Hiring
          </button>
        </div>
      </div>
    </div>
  )
}

export default Nav;
