"use client";
import React, { useState } from "react";
import useButtonAction from "../../hooks/useButtonAction";

const Hero = () => {
  const [openIndex, setOpenIndex] = useState(null); // Track the index of the open question
  const { handleBookCall } = useButtonAction();

  // Sample FAQs data
  const faqs = [
    {
      question: "How do you find qualified candidates?",
      answer:
        "Our return policy lasts 30 days. If 30 days have gone by since your purchase, unfortunately we can’t offer you a refund or exchange.",
    },
    {
      question: "How do you vet applicants?",
      answer:
        "Shipping usually takes 5-7 business days for standard delivery. Expedited shipping is available at checkout.",
    },
    {
      question: "Can I hire part-time?",
      answer: "Yes, we ship to over 50 countries around the world!",
    },
    {
      question: "Do I need to sponsor candidates with a work Visa?",
      answer:
        "Once your order is shipped, you will receive a tracking number via email to monitor the delivery status.",
    },
    {
      question: "How do I pay candidates?",
      answer:
        "Once your order is shipped, you will receive a tracking number via email to monitor the delivery status.",
    },
  ];

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index); // Toggle the open/close state
  };

  return (
    <div className="w-full max-w-[1440px] xl:w-[1440px] mx-auto min-h-[400px] xl:h-[648px] py-[56px] xl:py-[112px] px-4 sm:px-8 xl:px-[64px] bg-[#FFFFFF]">
      <div className="w-full max-w-[1312px] xl:w-[1312px] mx-auto min-h-[350px] xl:h-[424px] flex flex-col xl:flex-row gap-[40px] xl:gap-[80px]">
        <div className="w-full xl:w-[500px] flex flex-col gap-[24px] xl:gap-[32px] text-center xl:text-left">
          <div className="flex flex-col gap-[16px] xl:gap-[24px]">
            <h2 className="font-semibold text-[32px] sm:text-[40px] xl:text-[48px] leading-[1.2] xl:leading-[57.6px] text-[#000000]">
              FAQs
            </h2>
            <p className="font-normal text-[16px] xl:text-[18px] leading-[1.5] xl:leading-[27.6px] text-[#666666] max-w-[400px] xl:max-w-none mx-auto xl:mx-0">
              Still have questions? Book a call to learn more
            </p>
          </div>
          <div className="flex justify-center xl:justify-start">
            <button 
              className="w-[132px] h-[48px] rounded-[12px] border-[1px] border-[#000000] py-[12px] px-[24px] flex items-center justify-center transition-all hover:bg-[#000000] hover:text-white"
              onClick={handleBookCall}
            >
              <span className="font-light text-[15px] leading-[24px]">Book a call</span>
            </button>
          </div>
        </div>

        <div className="w-full xl:w-[732px] flex flex-col gap-[16px]">
          {faqs.map((faq, index) => (
            <div key={index} className="rounded-[12px] overflow-hidden">
              <div
                className="w-full font-semibold flex justify-between items-center py-[15px] px-[16px] xl:px-[24px] rounded-[12px] bg-[#F7F7F7] cursor-pointer"
                onClick={() => toggleFAQ(index)}
                aria-expanded={openIndex === index}
                aria-controls={`faq-${index}`}
              >
                <h3 className="text-[16px] xl:text-lg font-medium pr-4">{faq.question}</h3>
                <span className="w-[24px] h-[24px] xl:w-[28px] xl:h-[28px] flex items-center justify-center text-[24px] xl:text-[32px] font-extralight">
                  {openIndex === index ? "-" : "+"}
                </span>
              </div>
              <div
                id={`faq-${index}`}
                className={`transition-all duration-300 ease-in-out ${
                  openIndex === index ? "max-h-[200px] py-[12px]" : "max-h-0"
                }`}
              >
                <p className="text-gray-600 font-extralight px-[16px] xl:px-[24px] text-[14px] xl:text-base">
                  {faq.answer}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
