"use client";
import React, { useState } from 'react';
import Slide from './slide';

function Carousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const slides = [
    {
      title: "We were blown away by how quickly we were matched with qualified candidates. Our Marketing Assistant was an obvious choice and we've been incredibly happy working with her. One of the best investments we've made in our business.",
      description: "Corey Haines",
      additionalDescription: "Cofounder, Conversion Factory",
      logo: "/Testimonial/conversion-factory-logo-white-rgb 1.png",
      additionalImage: "/Testimonial/Avatar Image.png"
    },
    {
      title: "The talent pool from Latin Remote Workers is top-notch. Our new remote hires exceeded all expectations.",
      description: "Constantine Yaremtso",
      additionalDescription: "CEO, Self Inspection",
      logo: "/Testimonial/self inspection logo.png",
      additionalImage: "/Testimonial/Avatar Image 3.png"
    },
    {
      title: "Latin Remote Workers made hiring remote talent effortless. The team they helped us hire has been a game-changer.",
      description: "Brian Nelson",
      additionalDescription: "President, South Coast Commercial",
      logo: "/Testimonial/Untitled.png",
      additionalImage: "/Testimonial/AvatarImage2.png"
    }
  ];

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : slides.length - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex < slides.length - 1 ? prevIndex + 1 : 0));
  };

  return (
    <div className='w-full max-w-[1440px] mx-auto min-h-[400px] lg:min-h-[676px] py-[56px] lg:py-[112px] px-4 sm:px-8 lg:px-[64px]' id="testimonials">
      {/* Section Header */}
      <div className="text-center mb-[40px] lg:mb-[80px]">
        <div className="font-semibold text-[14px] leading-[21px] text-[#666666] uppercase tracking-wider mb-4">
          TESTIMONIALS
        </div>
        <h2 className="font-semibold text-[28px] sm:text-[36px] lg:text-[48px] leading-[1.2] text-[#000000]">
          What Our Clients Say
        </h2>
      </div>

      {/* Carousel Container */}
      <div className="relative flex items-center justify-center">
        {/* Previous Button */}
        <button 
          onClick={handlePrevious} 
          className="absolute left-0 z-10 w-10 h-10 lg:w-12 lg:h-12 rounded-full bg-white shadow-lg border border-gray-200 flex items-center justify-center transition-all hover:bg-gray-50 hover:scale-110"
          aria-label="Previous testimonial"
        >
          <svg className="w-5 h-5 lg:w-6 lg:h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        {/* Slides Container */}
        <div className="overflow-hidden mx-8 lg:mx-16 w-full max-w-[800px]">
          <div 
            className="flex transition-transform duration-300 ease-in-out"
            style={{ transform: `translateX(-${currentIndex * 100}%)` }}
          >
            {slides.map((slide, index) => (
              <div key={index} className="w-full flex-shrink-0">
                <Slide
                  title={slide.title}
                  description={slide.description}
                  additionalDescription={slide.additionalDescription}
                  image={slide.logo}
                  additionalImage={slide.additionalImage}
                />
              </div>
            ))}
          </div>
        </div>

        {/* Next Button */}
        <button 
          onClick={handleNext} 
          className="absolute right-0 z-10 w-10 h-10 lg:w-12 lg:h-12 rounded-full bg-white shadow-lg border border-gray-200 flex items-center justify-center transition-all hover:bg-gray-50 hover:scale-110"
          aria-label="Next testimonial"
        >
          <svg className="w-5 h-5 lg:w-6 lg:h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>
      </div>

      {/* Dots Indicator */}
      <div className="flex justify-center space-x-2 mt-6 lg:mt-8">
        {slides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentIndex(index)}
            className={`w-2 h-2 lg:w-3 lg:h-3 rounded-full transition-all ${
              index === currentIndex ? 'bg-[#000000]' : 'bg-gray-300 hover:bg-gray-400'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
}

export default Carousel;
