import { useCallback } from 'react';

const useButtonAction = () => {
  const handleStartHiring = useCallback(() => {
    console.log('Start Hiring clicked');
    if (window.gtag) {
      window.gtag('event', 'click', {
        'event_category': 'CTA',
        'event_label': 'Start Hiring'
      });
    }
    const contactSection = document.getElementById('contact');
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: 'smooth' });
    } else {
      window.open('https://calendly.com/your-company/hiring-consultation', '_blank');
    }
  }, []);

  const handleBookCall = useCallback(() => {
    console.log('Book a Call clicked');
    if (window.gtag) {
      window.gtag('event', 'click', {
        'event_category': 'CTA',
        'event_label': 'Book a Call'
      });
    }
    window.open('https://calendly.com/your-company/consultation', '_blank');
  }, []);

  const handleNavigation = useCallback((section) => {
    console.log(`Navigation to ${section} clicked`);
    if (window.gtag) {
      window.gtag('event', 'click', {
        'event_category': 'Navigation',
        'event_label': section
      });
    }

    const sectionMap = {
      'how-it-works': 'how-it-works',
      'pricing': 'pricing',
      'roles': 'roles',
      'our-promise': 'testimonials'
    };

    const targetId = sectionMap[section.toLowerCase().replace(/\s+/g, '-')];
    const targetElement = document.getElementById(targetId);

    if (targetElement) {
      targetElement.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  return {
    handleStartHiring,
    handleBookCall,
    handleNavigation
  };
};

export default useButtonAction;
