import React, { useState } from 'react'
import CompanyLogo from '../icons/company-logo'
import useButtonAction from '../../hooks/useButtonAction'
import './index.css'

const Nav = () => {
  const { handleStartHiring, handleNavigation, handleHome } = useButtonAction();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <>
      {/* Original design for screens >= 768px */}
      <div className='gradient-border items-center justify-between px-[64px] h-[72px] w-[1440px] m-[auto] hidden md:flex'>
        <div onClick={handleHome} className="cursor-pointer">
          <CompanyLogo />
        </div>
        <div className='flex items-center gap-[32px]'>
          <span 
            className='cursor-pointer underline-custom'
            onClick={() => handleNavigation('how-it-works')}
          > 
            How it works
          </span>
          <span 
            className='cursor-pointer underline-custom'
            onClick={() => handleNavigation('pricing')}
          >
            Pricing
          </span>
          <span 
            className='cursor-pointer underline-custom'
            onClick={() => handleNavigation('roles')}
          >
            Roles
          </span>
          <span 
            className="cursor-pointer underline-custom"
            onClick={() => handleNavigation('our-promise')}
          >
            Our promise
          </span>
          <button 
            className='text-white px-[20px] py-[8px] rounded-[10px] bg-[#231F20] transition-all hover:bg-[#3a3a3a] hover:scale-105'
            onClick={handleStartHiring}
          >
            Start Hiring
          </button>
        </div>
      </div>

      {/* Mobile version for screens < 768px */}
      <div className='gradient-border flex items-center justify-between px-4 h-[72px] w-full relative md:hidden'>
        <div onClick={handleHome} className="cursor-pointer">
          <CompanyLogo />
        </div>

        <button 
          className='flex flex-col gap-1 p-2'
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <span className='w-6 h-0.5 bg-black transition-all'></span>
          <span className='w-6 h-0.5 bg-black transition-all'></span>
          <span className='w-6 h-0.5 bg-black transition-all'></span>
        </button>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className='absolute top-[72px] left-0 w-full bg-white shadow-lg z-50 border-t'>
            <div className='flex flex-col p-4 gap-4'>
              <span 
                className='cursor-pointer py-2 border-b'
                onClick={() => {
                  handleNavigation('how-it-works');
                  setIsMenuOpen(false);
                }}
              > 
                How it works
              </span>
              <span 
                className='cursor-pointer py-2 border-b'
                onClick={() => {
                  handleNavigation('pricing');
                  setIsMenuOpen(false);
                }}
              >
                Pricing
              </span>
              <span 
                className='cursor-pointer py-2 border-b'
                onClick={() => {
                  handleNavigation('roles');
                  setIsMenuOpen(false);
                }}
              >
                Roles
              </span>
              <span 
                className="cursor-pointer py-2 border-b"
                onClick={() => {
                  handleNavigation('our-promise');
                  setIsMenuOpen(false);
                }}
              >
                Our promise
              </span>
              <button 
                className='text-white px-[20px] py-[8px] rounded-[10px] bg-[#231F20] w-full mt-2'
                onClick={() => {
                  handleStartHiring();
                  setIsMenuOpen(false);
                }}
              >
                Start Hiring
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  )
}

export default Nav;
